import { useState } from "react";
import axios from "axios";

const AddActivity = () => {
  const [activity, setActivity] = useState({
    name: "",
    difficulty: "",
    duration: "",
    season: "",
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setActivity({ ...activity, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const res = await axios.post("http://localhost:3000", activity);
      console.log(res.data); // muestra la actividad creada en la consola
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <h2>Agregar actividad</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Nombre:</label>
          <input type="text" id="name" name="name" onChange={handleInputChange} value={activity.name} autocomplete="off" />
        </div>
        <div>
          <label htmlFor="difficulty">Dificultad:</label>
          <select id="difficulty" name="difficulty" onChange={handleInputChange} value={activity.difficulty}>
            <option value="">Selecciona una opción</option>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="4">4</option>
            <option value="5">5</option>
          </select>
        </div>
        <div>
          <label htmlFor="duration">Duración:</label>
          <input type="number" id="duration" name="duration" onChange={handleInputChange} value={activity.duration} />
        </div>
        <div>
          <label htmlFor="season">Temporada:</label>
          <select id="season" name="season" onChange={handleInputChange} value={activity.season}>
            <option value="">Selecciona una opción</option>
            <option value="Summer">Verano</option>
            <option value="Autumn">Otoño</option>
            <option value="Spring">Primavera</option>
            <option value="Winter">Invierno</option>
            <option value="All the year">Todo el año</option>
          </select>
        </div>
        <button type="submit">Agregar actividad</button>
      </form>
    </div>
  );
};

export default AddActivity;
