import { useState } from "react";

const AddActivity = () => {
  const [formData, setFormData] = useState({
    name: "",
    difficulty: "",
    duration: "",
    season: "",
    countriesName: [],
  });

  const handleChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleCountryChange = (event) => {
    const options = event.target.options;
    const selectedCountries = [];
    for (let i = 0; i < options.length; i++) {
      if (options[i].selected) {
        selectedCountries.push(options[i].value);
      }
    }
    setFormData({
      ...formData,
      countriesName: selectedCountries,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch("http://localhost:3001/activities", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      console.log(data);
      // Aquí podrías mostrar un mensaje de éxito o redireccionar a otra página
    } catch (error) {
      console.error(error);
      // Aquí podrías mostrar un mensaje de error al usuario
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input 
          type="text"
          name="name"
          placeholder="Ingrese una actividad"
          value={formData.name}
          onChange={handleChange}
          required
          minLength={5}
          maxLength={100}
        />
      </label>
      <br />
      <label>
        Difficulty:
        <input
          type="text"
          name="difficulty"
          value={formData.difficulty}
          onChange={handleChange}
          required
          pattern="[1-5]"
        />
      </label>
      <br />
      <label>
        Duration:
        <input
          type="text"
          name="duration"
          value={formData.duration}
          onChange={handleChange}
          required
          pattern="\d+(\.\d{1,2})?"
        />
      </label>
      <br />
      <label>
        Season:
        <select
          name="season"
          value={formData.season}
          onChange={handleChange}
          required
        >
          <option value="">Select a season</option>
          <option value="Summer">Summer</option>
          <option value="Autumn">Autumn</option>
          <option value="Spring">Spring</option>
          <option value="Winter">Winter</option>
          <option value="All the year">All the year</option>
        </select>
      </label>
      <br />
      <label>
        Countries:
        <select
          name="countriesName"
          value={formData.countriesName}
          onChange={handleCountryChange}
          multiple
        >
          <option value="Argentina">Argentina</option>
          <option value="Brasil">Brasil</option>
          <option value="Chile">Chile</option>
          <option value="Uruguay">Uruguay</option>
        </select>
      </label>
      <br />
      <button type="submit">Create Activity</button>
    </form>
  );
};

export default AddActivity;
