import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import FilterCountry from "../FilterCountry/FilterCountry";

const AllCountries = () => {
  const [countries, setCountries] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [regionFilter, setRegionFilter] = useState("");

  const getAllCountries = async () => {
    setIsLoading(true);
    setError("");

    try {
      let url = "http://localhost:3001/countries";
      if (regionFilter) {
        url += `?region=${regionFilter}`;
      }
      const res = await axios.get(url);
      setCountries(res.data);
      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
      setError(error.message);
    }
  };

  useEffect(() => {
    getAllCountries();
  }, [regionFilter]);

  const handleSearchQueryChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleRegionFilterChange = (region) => {
    setRegionFilter(region);
  };

  const filteredCountries = countries.filter((country) => {
    if (!searchQuery) {
      return true;
    }
    return country.name.toLowerCase().includes(searchQuery.toLowerCase());
  });

  return (
    <div className="all__Country__wrapper">
      <div className="country__top">
        <div className="search-input-container">
          <Link to="/" className="home">
            <button>
              <p>-=Home=-</p>
            </button>
          </Link>
        </div>
        <div className="search">
          <input
            placeholder="Escribe aqui el pais🌍"
            type="search"
            value={searchQuery}
            onChange={handleSearchQueryChange}
          />
        </div>
        <div className="filter">
          <FilterCountry onSelect={handleRegionFilterChange} />
        </div>
      </div>
      <div className="country__bottom">
        {isLoading && !error && <h4>Loading.........</h4>}
        {error && !isLoading && <h4>{error}</h4>}

        {filteredCountries.map((country, index) => (
          <div className="country__card" key={index}>
            <div className="country__img">
              <img src={country.flag} alt="png" />
            </div>

            <div className="country__data">
              <h3>{country.name}</h3>
              <h6>Population: {country.population}</h6>
              <h6>Region: {country.region}</h6>
              <h6>Capital: {country.capital}</h6>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AllCountries;
