import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import FilterCountry from "../FilterCountry/FilterCountry";
import Pagination from "../Paginado/Pagination";

const AllCountries = () => {
  const [countries, setCountries] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const [SelecterBuscador, setSelecterBuscador] = useState("");
  const [SelectRegion, setSelectRegion] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [countriesPerPage] = useState(12);

  const filteredCountries = SelectRegion === "" ? countries : countries.filter((country) => country.region === SelectRegion);

  const handleRegionChange = (regionName) => {
    setSelectRegion(regionName);
    setCurrentPage(1);
  };

  const alphabeticalFilter = (array) => {
    const filteredArray = array.filter((country) =>
      country.name.toLowerCase().startsWith(SelecterBuscador.toLowerCase())
    );
    return filteredArray.sort((a, b) => (a.name > b.name ? 1 : -1));
  };

  const filteredByNameAndRegion = alphabeticalFilter(filteredCountries);

  const getAllCountries = async () => {
    try {
      const res = await axios.get("http://localhost:3001/countries");
      if (res.status !== 200) throw new Error("algo salio mal");

      const data = res.data;
      setCountries(data);
      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
      setError(error.message);
    }
  };

  const handlerBuscadorChange = (e) => {
    setSelecterBuscador(e.target.value);
    setCurrentPage(1);
  };

  useEffect(() => {
    getAllCountries();
  }, []);

  const indexOfLastCountry = currentPage * countriesPerPage;
  const indexOfFirstCountry = indexOfLastCountry - countriesPerPage;
  const currentCountries = filteredByNameAndRegion.slice(indexOfFirstCountry, indexOfLastCountry);

  const handlePageChange = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="all__Country__wrapper">
      <div className="country__top">
        <div className="search-input-container">
          <Link to="/" className="home">
            <button>
              <p> -=Home=-</p>
            </button>
          </Link>
          <div className="Activity">
            <Link to="/activities">
              <button>Crea tu actividad</button>
            </Link>
          </div>
        </div>
        <div className="search">
          <input placeholder="Escribe aqui el paisðŸŒ" type="search" onChange={handlerBuscadorChange} />
        </div>
        <div className="filter">
          <FilterCountry onSelect={handleRegionChange} />
        </div>
      </div>
      <div className="country__bottom">
        {isLoading && !error && <h4>Loading.........</h4>}
        {error && !isLoading && <h4>{error}</h4>}

        {currentCountries?.length === 0 ? (
          <h4>No se encontraron resultados</h4>
        ) : (
          <>
            <alphabeticalFilter data={filteredByNameAndRegion} />
            {currentCountries.map((country, index) => (
              <Link to={`/countries/${country.name}`} key={index} className="country__card">
                <div className="country__img">
                  <img src={country.flag} alt="png" />
                </div>
                <div className="country__data">
                  <h3>{country.name}</h3>
                  <h6>Population: {country.population}</h6>
                  <h6>Region: {country.region}</h6>
                  <h6>Capital: {country.capital}</h6>
                </div>
              </Link>
            ))}
            {filteredByNameAndRegion.length > 10 && (
              <Pagination data={filteredByNameAndRegion} itemsPerPage={countriesPerPage} onPageChange={handlePageChange} />
            )}
          </>
        )}
      </div>
    </div>
  );
            }

export default AllCountries;
