import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import FilterCountry from "../FilterCountry/FilterCountry";
import Pagination from "../Paginado/Pagination";

const AllCountries = () => {
  const [countries, setCountries] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const [SelecterBuscador, setSelecterBuscador] = useState("");
  const [SelectRegion, setSelectRegion] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(8); // 8 elementos por página

  const filteredCountries = SelectRegion === "" ? countries : countries.filter(country => country.region === SelectRegion);

  const handleRegionChange = (regionName) => {
    setSelectRegion(regionName);
    setCurrentPage(1); // Resetear la página actual al cambiar de región
  };

  const arrayfiltradoBandera = countries === "" ? countries : countries.filter(a => a.name.startsWith(SelecterBuscador));

  const filteredByNameAndRegion = arrayfiltradoBandera.filter(country => filteredCountries.includes(country));

  const getAllCountries = async () => {
    try {
      const res = await axios.get('http://localhost:3001/countries')
      if (res.status !== 200) throw new Error("algo salio mal");

      const data = res.data;
      setCountries(data);
      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
      setError(error.message);
    }
  };

  const handlerBuscadorChange = (e) => {
    setSelecterBuscador(e.target.value);
    setCurrentPage(1); // Resetear la página actual al cambiar el valor del buscador
  }

  useEffect(() => {
    getAllCountries();
  }, []);

  // Obtiene los elementos que se deben mostrar en la página actual
  const getPageData = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return filteredByNameAndRegion.slice(startIndex, endIndex);
  };

  // Cambia de página y actualiza la lista de países a mostrar
  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  return (
    <div className="all__Country__wrapper">
      <div className="country__top">
        <div className="search-input-container">
          <Link to="/" className="home">
            <button>
              <p> -=Home=-</p>
            </button>
          </Link>
        </div>
        <div className="search">
          <input placeholder="Escribe aqui el pais🌍" type="search" onChange={handlerBuscadorChange} />
        </div>
        <div className="filter">
          <FilterCountry onSelect={handleRegionChange} />
        </div>
      </div>
      <div className="country__bottom">
        {isLoading && !error && <h4>Loading.........</h4>}
        {error && !isLoading && <h4>{error}</h4>}

        {getPageData()?.map((country, index) => (
          <div className="country__card" key={index}>
            <div className="country__img">
              <img src={country.flag} alt="png"/>
            </div>
            <div className="country__data">
              <h3>{country.name
}</h3>
              <h6>Population: {country.population}</h6>
              <h6>Region: {country.region}</h6>
              <h6>Capital: {country.capital}</h6>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AllCountries;
