import React, { useState, useEffect } from "react";

import axios from "axios";
import SearchInput from "../Search/SearchInput";
import FilterCountry from "../FilterCountry/FilterCountry";


const AllCountries = () => {
const [countries, setCountries] = useState([]);
const [isLoading, setIsLoading] = useState(true);
const [error, setError] = useState("");

const getAllCountries = async () => {
try {
const res = await axios.get('http://localhost:3001/countries')



  if(res.status !== 200) throw new Error("algo salio mal");

  const data = res.data;

  console.log(data);

  setCountries(data);

  setIsLoading(false);
} catch (error) {
  setIsLoading(false);
  setError(error.message);
}
};
const getCountryByName = async (countryName) => {
  try {
    const res = await fetch(`http://localhost:3001/countries/${countryName}`);

    if (!res.ok) throw new Error("No se encontro ningun pais");

    const data = await res.json();
    setCountries(data);

    setIsLoading(false);
  } catch (error) {
    setIsLoading(false);
    setError(error.message);
  }
};
const [regionName, setRegionName] = useState("all");

const getCountryByRegion = async () =>{
  try {
    let url = "http://localhost:3001/countries";
    if (regionName !== "all") {
      url += `?region=${regionName}`;
    }
    const res = await fetch(url);
    if (!res.ok) throw new Error("hay un fallo ")
    const data = await res.json();
    setCountries(data);
    setIsLoading(false);
  } catch (error) {
    setIsLoading(false);
    setError(true);
  }
}

useEffect(() => {
  getCountryByRegion();
}, [regionName]);

const selectHandler = (e) => {
  const regionName = e.target.value;
  setRegionName(regionName);
}

return (
<div className="all__Country__wrapper">
  <div className="country__top">
  <div className="search">
     <SearchInput onSearch={getCountryByName}/>
</div>

<div className="filter">
   <FilterCountry onSelect={getCountryByRegion}/>
</div>

</div>
  <div className="country__bottom">
    {isLoading && !error && <h4>Loading.........</h4>}
    {error && !isLoading && <h4>{error}</h4>}

    {countries?.map((country, index) => (
  <div className="country__card" key={index}>
    <div className="country__img">
      <img src={country.flag} alt="png"/>
    </div>

    <div className="country__data">
      <h3>{country.name}</h3>
      <h6>Population: {country.population}</h6>
      <h6>Region: {country.region}</h6>
      <h6>Capital: {country.capital}</h6>
    </div>
  </div>
))}
  </div>
</div>
);
};

export default AllCountries;
