import React from 'react';
import { useSelector, useDispatch, Provider } from 'react-redux';
import { byContinent } from '../Redux/actions';
import { store } from '../Redux/store';

const FilterCountry = ({ onSelect }) => {
  const dispatch = useDispatch();
  const countriesByContinent = useSelector((state) => state.countriesByContinent);

  const selectHandler = (e) => {
    const regionName = e.target.value;
    onSelect(regionName);
    dispatch(byContinent(regionName));
  };

  return (
    <select onChange={selectHandler}>
      <option className="option">Filtrar por continente</option>
      <option className="option" value="Africa">
        Africa
      </option>
      <option className="option" value="Americas">
        Americas
      </option>
      <option className="option" value="Asia">
        Asia
      </option>
      <option className="option" value="Europe">
        Europe
      </option>
      <option className="option" value="Oceania">
        Oceania
      </option>
    </select>
  );
};

const FilterCountryProvider = () => {
  return (
    <Provider store={store}>
      <FilterCountry />
    </Provider>
  );
};

export default FilterCountryProvider;
