import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import FilterCountry from "../FilterCountry/FilterCountry";
import Pagination from "../Paginado/Pagination";

const AllCountries = () => {
  const [countries, setCountries] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const [SelecterBuscador ,setSelecterBuscador] = useState("");
  const [SelectRegion,setSelectRegion] = useState("");
  const [currentPage, setCurrentPage] = useState(1);

  const handleRegionChange = (regionName) => {
    setSelectRegion(regionName);
  };

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const arrayfiltradoBandera = countries === "" ? countries : countries.filter(a => a.name.startsWith(SelecterBuscador));

  const arrayfiltradoRegion =
    SelectRegion === "" ? countries : countries.filter(country => country.region === SelectRegion);

  const getAllCountries = async () => {
    try {
      const res = await axios.get('http://localhost:3001/countries')
      if (res.status !== 200) throw new Error("algo salio mal");

      const data = res.data;
      setCountries(data);
      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
      setError(error.message);
    }
  };

  const handlerBuscadorChange = (e) => {
    setSelecterBuscador(e.target.value);
  }

  useEffect(() => {
    getAllCountries();
  }, []);
  const itemsPerPage = 9;
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = arrayfiltradoRegion.slice(indexOfFirstItem, indexOfLastItem);

  return (
    <div>
      {error && <div>{error}</div>}
      {isLoading ? (
        <div>Loading...</div>
      ) : (
        <>
          <FilterCountry
            handleRegionChange={handleRegionChange}
            handlerBuscadorChange={handlerBuscadorChange}
          />
          <div className="container">
            {currentItems.map((country) => (
              <div className="card" key={country.alpha3Code}>
                <Link to={`/country/${country.alpha3Code}`}>
                  <img
                    src={country.flag}
                    alt={`Flag of ${country.name}`}
                    className="card-img-top"
                  />
                </Link>
                <div className="card-body">
                  <h5 className="card-title">{country.name}</h5>
                  <p className="card-text">
                    <strong>Population: </strong>
                    {country.population.toLocaleString()}
                  </p>
                  <p className="card-text">
                    <strong>Region: </strong>
                    {country.region}
                  </p>
                  <p className="card-text">
                    <strong>Capital: </strong>
                    {country.capital}
                  </p>
                </div>
              </div>
            ))}
          </div>
          <Pagination
            data={arrayfiltradoRegion}
            itemsPerPage={itemsPerPage}
            onPageChange={handlePageChange}
          />
        </>
      )}
    </div>
  );
};

export default AllCountries;
