import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";


const Details = () => {
  const { alpha3Code } = useParams();
  const [country, setCountry] = useState(null);

  useEffect(() => {
    axios
      .get(`https://restcountries.com/v2/alpha/${alpha3Code}`)
      .then((response) => {
        setCountry(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [alpha3Code]);

  return (
    <div className="details__wrapper">
      <div className="details__header">
        <Link to="/" className="home">
          <button>
            <p>{"<- Back"}</p>
          </button>
        </Link>
      </div>
      {country ? (
        <div className="details__body">
          <div className="details__flag">
            <img src={country.flags[0]} alt={country.name} />
          </div>
          <div className="details__info">
            <h2>{country.name}</h2>
            <div className="details__info--left">
              <p>
                <strong>Native Name: </strong>
                {country.nativeName}
              </p>
              <p>
                <strong>Population: </strong>
                {country.population.toLocaleString()}
              </p>
              <p>
                <strong>Region: </strong>
                {country.region}
              </p>
              <p>
                <strong>Sub Region: </strong>
                {country.subregion}
              </p>
              <p>
                <strong>Capital: </strong>
                {country.capital}
              </p>
            </div>
            <div className="details__info--right">
              <p>
                <strong>Top Level Domain: </strong>
                {country.topLevelDomain}
              </p>
              {country.currencies && (
                <p>
                  <strong>Currencies: </strong>
                  {country.currencies.map((currency) => currency.name).join(", ")}
                </p>
              )}
              {country.languages && (
                <p>
                  <strong>Languages: </strong>
                  {country.languages.map((language) => language.name).join(", ")}
                </p>
              )}
            </div>
          </div>
        </div>
      ) : (
        <div className="details__loading">
          <h4>Loading.........</h4>
        </div>
      )}
    </div>
  );
};

export default Details;
