import React, { useState, useEffect } from "react";
import axios from "axios";
import SearchInput from "../components/SearchInput";
import RegionSelect from "../components/RegionSelect";
import Pagination from "../components/Pagination";

const AllCountries = () => {
  const [allCountries, setAllCountries] = useState([]);
  const [searchInput, setSearchInput] = useState("");
  const [regionSelect, setRegionSelect] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [countriesPerPage] = useState(8);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAllCountries = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get("https://restcountries.com/v2/all");
        setAllCountries(response.data);
        setIsLoading(false);
      } catch (error) {
        setError("Oops! Something went wrong. Please try again later.");
        setIsLoading(false);
      }
    };
    fetchAllCountries();
  }, []);

  const handleSearchInputChange = (event) => {
    setSearchInput(event.target.value);
    setCurrentPage(1);
  };

  const handleRegionSelectChange = (event) => {
    setRegionSelect(event.target.value);
    setCurrentPage(1);
  };

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const indexOfLastCountry = currentPage * countriesPerPage;
  const indexOfFirstCountry = indexOfLastCountry - countriesPerPage;

  const filteredByNameAndRegion = allCountries
    .filter((country) =>
      country.name.toLowerCase().includes(searchInput.toLowerCase())
    )
    .filter((country) =>
      regionSelect === "" ? true : country.region === regionSelect
    );

  const currentCountries = filteredByNameAndRegion.slice(
    indexOfFirstCountry,
    indexOfLastCountry
  );

  return (
    <div className="container">
      <div className="search-filters">
        <SearchInput
          searchInput={searchInput}
          handleSearchInputChange={handleSearchInputChange}
        />
        <RegionSelect
          regionSelect={regionSelect}
          handleRegionSelectChange={handleRegionSelectChange}
        />
      </div>

      <div className="country__bottom">
        {isLoading && !error && <h4>Loading.........</h4>}
        {error && !isLoading && <h4>{error}</h4>}

        {currentCountries?.map((country, index) => (
          <div className="country__card" key={index}>
            <div className="country__img">
              <img src={country.flag} alt="png" />
            </div>
            <div className="country__data">
              <h3>{country.name}</h3>
              <h6>Population: {country.population}</h6>
              <h6>Region: {country.region}</h6>
              <h6>Capital: {country.capital}</h6>
            </div>
          </div>
        ))}
        {filteredByNameAndRegion.length > 10 && (
          <Pagination
            data={filteredByNameAndRegion}
            itemsPerPage={countriesPerPage}
            onPageChange={handlePageChange}
          />
        )}
      </div>
    </div>
  );
};

export default AllCountries;
