// actions.js

// Definimos una constante para cada acción

export const SET_CONTINENT = "SET_CONTINENT";

// Función que crea la acción para actualizar el continente seleccionado
export const setContinent = (continent) => {
  return {
    type: SET_CONTINENT,
    payload: continent,
  };
};
