import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Activity = () => {
  const [activities, setActivities] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3001/activities')
      .then(response => setActivities(response.data))
      .catch(error => console.log(error));
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    const { name, difficulty, duration, season, countriesName } = event.target.elements;

    const activityData = {
      name: name.value,
      difficulty: difficulty.value,
      duration: duration.value,
      season: season.value,
      countriesName: countriesName.value.split(',').map(country => country.trim())
    };

    axios.post('http://localhost:3001/activities', activityData)
      .then(response => console.log(response))
      .catch(error => console.log(error));
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Nombre de la actividad" />
        <input type="text" name="difficulty" placeholder="Dificultad" />
        <input type="text" name="duration" placeholder="Duración" />
        <input type="text" name="season" placeholder="Temporada" />
        <input type="text" name="countriesName" placeholder="Países asociados (separados por comas)" />
        <button type="submit">Agregar actividad</button>
      </form>
      <ul>
        {activities.map(activity => (
          <li key={activity.id}>
            <h2>{activity.name}</h2>
            <p>Dificultad: {activity.difficulty}</p>
            <p>Duración: {activity.duration}</p>
            <p>Temporada: {activity.season}</p>
            <ul>
              {activity.countries.map(country => (
                <li key={country.id}>{country.name}</li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Activity;
