import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Link } from 'react-router-dom';

const CountryDetail = () => {
  const [country, setCountry] = useState([]);
 const [activities, setActivities] = useState([]);
  const { id } = useParams();
  console.log(country)
  useEffect(() => {
    const fetchData = async () => {

      try {
        const countryRes = await axios.get(`http://localhost:3001/countries/${id}`);

        setCountry(countryRes.data);
      const activitiesRes = await axios.get(`http://localhost:3001/countries/${id}/activities`);
       setActivities(activitiesRes.data);
      } catch (error) {

        console.log(error);
      }
    };
    fetchData();
  }, [id]);
console.log(country[0]?.name)
  if (!country) return <div>Loading...</div>;

  let activitiesList = [];

  activities.forEach((activity, index) => {
   activitiesList.push(<li key={index}>{activity}</li>);
  });

  let jsonSerialize = function() { //lo muestro como un objeto json 
    return JSON.stringify(activities)
  }


  return (
    <div className='detalles'>
      <h2>{country.name}</h2>
      <img src={country.flag} alt={`${country.name} flag`} />
      <p>Capital: {country.capital}</p>
      <p>Subregion: {country.subregion}</p>
      <h3>Activities:</h3>
      {country.activities && country.activities.map(activity => (
        <p key={activity.name}>{activity.name} ({activity.difficulty})</p>
      ))}

      <Link to='/countries'>
        <button>Ver todos los pa√≠ses</button>
      </Link>
    </div>
  );
};
export default CountryDetail;
