import React, { useState, useEffect } from "react";
import axios from "axios";
import SearchInput from "../Search/SearchInput";
import FilterCountry from "../FilterCountry/FilterCountry";

const AllCountries = () => {
  const [countries, setCountries] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");

  const getAllCountries = async () => {
    try {
      const res = await axios.get('http://localhost:3001/countries')

      if(res.status !== 200) throw new Error("algo salio mal");

      const data = res.data;

      // Get flags for all countries
      const flagRequests = data.map(country => {
        return axios.get(`https://restcountries.com/v3.1/name/${country.name.common}?fields=flags`)
      })
      const flagResponses = await Promise.all(flagRequests)
      const flags = flagResponses.map(response => response.data[0].flags[1])

      // Add flag property to each country object
      const countriesWithFlags = data.map((country, index) => {
        return { ...country, flag: flags[index] }
      })

      setCountries(countriesWithFlags);

      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
      setError(error.message);
    }
  };

  const getCountryByName = async (countryName) => {
    try {
      const res = await axios.get(
        `https://restcountries.com/v3/name/${countryName}`
      );

      if (res.status !== 200) throw new Error("No se encontró ningún país");

      const data = res.data;

      setCountries(data);

      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
      setError(error.message);
    }
  };

  const getCountryByRegion = async (regionName) => {
    try {
      const res = await axios.get(
        `https://restcountries.com/v3/region/${regionName}`
      );

      if (res.status !== 200) throw new Error("Hay un fallo ");

      const data = res.data;

      setCountries(data);

      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
      setError(error.message);
    }
  };

  useEffect(() => {
    console.log("Hola");
    getAllCountries();
  }, []);


  return (
    <div className="all__Country__wrapper">
      <div className="country__top">
        <div className="search">
          <SearchInput onSearch={getCountryByName} />
        </div>

        <div className="filter">
          <FilterCountry onSelect={getCountryByRegion} />
        </div>
      </div>
      <div className="country__bottom">
        {isLoading && !error && <h4>Loading.........</h4>}
        {error && !isLoading && <h4>{error}</h4>}

        {countries?.map((country, index) => (
          <div className="country__card" key={index}>
            <div className="country__img">
            <img src={country.flag} alt="png"/>
            </div>

            <div className="country__data">
              <h3>{country.name.common}</h3>
              <h6>Population: {country.population}</h6>
              <h6>Region: {country.region}</h6>
              <h6>Capital: {country.capital}</h6>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AllCountries;
