import { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const AddActivity = () => {
  const [activity, setActivity] = useState({
    name: "",
    difficulty: "",
    duration: "",
    season: "",
    countries: [], // Agregamos un campo para almacenar los países
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setActivity({ ...activity, [name]: value });
  };

  const handleCountryChange = (event, index) => {
    const { value } = event.target;
    const countries = [...activity.countries];
    countries[index] = value;
    setActivity({ ...activity, countries });
  };

  const handleAddCountry = () => {
    setActivity({ ...activity, countries: [...activity.countries, ""] });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const res = await axios.post("http://localhost:3001/activities", activity);
      console.log(res.data); // muestra la actividad creada en la consola
    } catch (error) {
      ;
    }
  };

  return (

    <div className="AGREGAR-ACTIVITY">


      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">  ➤Nombre:</label>
          <input type="text" id="name" name="name" placeholder="Ingrese su actividad.." onChange={handleInputChange}  value={activity.name}  autoComplete="off" />
        </div>
        <div>
          <label htmlFor="difficulty">➤Dificultad:</label>
          <select id="difficulty" name="difficulty" onChange={handleInputChange} value={activity.difficulty}>
            <option value="">Selecciona una dificultad</option>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="4">4</option>
            <option value="5">5</option>
          </select>
        </div>
        <div>
          <label htmlFor="duration">➤Duración:</label>

          <select type="number" id="duration" name="duration" onChange={handleInputChange} value={activity.duration} >
          <option value="">Selecciona una opción</option>
            <option value="1">1hs</option>
            <option value="2">2hs</option>
            <option value="3">3hs</option>
            <option value="4">4hs</option>
            <option value="5">5hs</option>

            <option value="6">6hs</option>
            <option value="7">7hs</option>
            <option value="8">8hs</option>
            <option value="9">9hs</option>
            <option value="10">10hs</option>

            <option value="1">11hs</option>
            <option value="2">12hs</option>
            <option value="3">13hs</option>
            <option value="4">14hs</option>
            <option value="5">15hs</option>

            <option value="1">16hs</option>
            <option value="2">17hs</option>
            <option value="3">18hs</option>
            <option value="4">19hs</option>
            <option value="5">20hs</option>

            <option value="1">21hs</option>
            <option value="2">22hs</option>
            <option value="3">23hs</option>
            <option value="4">24hs</option>



          </select>
        </div>
        <div>
          <label htmlFor="season">➤Temporada:</label>
          <select id="season" name="season" onChange={handleInputChange} value={activity.season}>
            <option value="">Seleccione una temporada</option>
            <option value="Summer">Verano</option>
            <option value="Autumn">Otoño</option>
            <option value="Spring">Primavera</option>
            <option value="Winter">Invierno</option>
            <option value="All the year">Todo el año</option>
          </select>
        </div>
        <button type="submit">Agregar actividad</button>
      <Link to="/countries">





  <button>Back</button>
</Link>
      </form>
    </div>
  );
};

export default AddActivity;
